import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useHistory } from 'react-router-dom';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './registrationPage.css';

const RegistrationPage = () => {
  const { id } = useParams();
  const location = useLocation();
  const history = useHistory();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [step, setStep] = useState('registration'); // 'registration' or 'payment'
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  // Registration form state
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    country: '',
    zip: ''
  });

  // Payment form state
  const [paymentData, setPaymentData] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardHolder: ''
  });

  useEffect(() => {
    if (location.state?.event) {
      setEvent(location.state.event);
      setLoading(false);
    } else {
      fetchEventById(id);
    }
  }, [id, location.state]);

  const fetchEventById = async (eventId) => {
    try {
      const response = await fetch(`/api/events/${eventId}`);
      if (!response.ok) {
        throw new Error('Event not found');
      }
      const eventData = await response.json();
      setEvent(eventData);
    } catch (err) {
      setError(err.message);
      setTimeout(() => {
        history.push('/not-found');
      }, 3000);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePaymentChange = (e) => {
    const { name, value } = e.target;
    setPaymentData(prev => ({ ...prev, [name]: value }));
  };

  const handleRegistrationSubmit = (e) => {
    e.preventDefault();
    setStep('payment');
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();
    // Simulate payment processing
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setPaymentSuccess(true);
      // Update event status in backend
      updateEventStatus(id);
    }, 1000);
  };

  const updateEventStatus = async (eventId) => {
    try {
      await fetch(`/api/events/${eventId}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });
    } catch (err) {
      console.error('Error updating event status:', err);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!event) {
    return <div>Event not found</div>;
  }

  return (
    <div className="registration-container">
      <Navbar />
      <main className="registration-content">
        <div className="registration-grid">
          {/* Left Side - Form */}
          <div className="registration-form">
            {step === 'registration' ? (
              <>
                <h2>Register for {event.title}</h2>
                <form onSubmit={handleRegistrationSubmit}>
                  {/* Registration form fields */}
                  <div className="registration-form-group">
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Full Name"
                      required
                    />
                  </div>
                  {/* Other registration fields */}
                  <button type="submit" className="registration-submit-button">
                    Continue to Payment
                  </button>
                </form>
              </>
            ) : (
              <>
                <h2>Payment for {event.title}</h2>
                {paymentSuccess ? (
                  <div className="payment-success">
                    <h3>Payment Successful!</h3>
                    <p>You are now registered for {event.title}</p>
                  </div>
                ) : (
                  <form onSubmit={handlePaymentSubmit}>
                    <div className="registration-form-group">
                      <input
                        type="text"
                        name="cardNumber"
                        value={paymentData.cardNumber}
                        onChange={handlePaymentChange}
                        placeholder="Card Number"
                        required
                      />
                    </div>
                    <div className="registration-form-row">
                      <div className="registration-form-group">
                        <input
                          type="text"
                          name="expiryDate"
                          value={paymentData.expiryDate}
                          onChange={handlePaymentChange}
                          placeholder="MM/YY"
                          required
                        />
                      </div>
                      <div className="registration-form-group">
                        <input
                          type="text"
                          name="cvv"
                          value={paymentData.cvv}
                          onChange={handlePaymentChange}
                          placeholder="CVV"
                          required
                        />
                      </div>
                    </div>
                    <div className="registration-form-group">
                      <input
                        type="text"
                        name="cardHolder"
                        value={paymentData.cardHolder}
                        onChange={handlePaymentChange}
                        placeholder="Card Holder Name"
                        required
                      />
                    </div>
                    <button type="submit" className="registration-submit-button">
                      Complete Payment
                    </button>
                  </form>
                )}
              </>
            )}
          </div>

          {/* Right Side - Event Details */}
          <div className="registration-event-details">
            <div className="registration-event-image-container">
              <img src={event.image} alt={event.title} className="registration-event-image" />
            </div>
            <div className="registration-event-info">
              <h3>{event.title}</h3>
              <p className="registration-event-description">{event.description}</p>
              <div className="registration-event-meta">
                <p><strong>Date:</strong> {event.date}</p>
                <p><strong>Time:</strong> {event.time}</p>
                <p><strong>Location:</strong> {event.location}</p>
              </div>
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default RegistrationPage;
