import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useHistory } from 'react-router-dom';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './registrationPage.css';

const RegistrationPage = () => {
  const { id } = useParams();
  const location = useLocation();
  const history = useHistory();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Check if event data was passed via location state
    if (location.state?.event) {
      setEvent(location.state.event);
      setLoading(false);
    } else {
      // If no event data was passed, fetch it using the ID
      fetchEventById(id);
    }
  }, [id, location.state]);

  const fetchEventById = async (eventId) => {
    try {
      // Replace with your actual API endpoint
      const response = await fetch(`/api/events/${eventId}`);
      if (!response.ok) {
        throw new Error('Event not found');
      }
      const eventData = await response.json();
      setEvent(eventData);
    } catch (err) {
      setError(err.message);
      // Redirect to not found page after 3 seconds
      setTimeout(() => {
        history.push('/not-found');
      }, 3000);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading event details...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!event) {
    return <div>Event not found</div>;
  }

  // Rest of the component remains the same
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    country: '',
    zip: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
    // Handle form submission
  };

  return (
    <div className="registration-container">
      <Navbar />
      <main className="registration-content">
        <div className="registration-grid">
          {/* Left Side - Registration Form */}
          <div className="registration-form">
            <h2>Register for {event.title}</h2>
            <form onSubmit={handleSubmit}>
              {/* Form fields remain the same */}
            </form>
          </div>

          {/* Right Side - Event Details */}
          <div className="registration-event-details">
            <div className="registration-event-image-container">
              <img src={event.image} alt={event.title} className="registration-event-image" />
            </div>
            <div className="registration-event-info">
              <h3>{event.title}</h3>
              <p className="registration-event-description">{event.description}</p>
              <div className="registration-event-meta">
                <p><strong>Date:</strong> {event.date}</p>
                <p><strong>Time:</strong> {event.time}</p>
                <p><strong>Location:</strong> {event.location}</p>
              </div>
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default RegistrationPage;
