import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useHistory } from 'react-router-dom';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './registrationPage.css';

const Loading = () => (
  <div className="loading-container">
    <div className="loading-circle"></div>
  </div>
);

const RegistrationPage = () => {
  const { id } = useParams();
  const location = useLocation();
  const history = useHistory();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [step, setStep] = useState('registration');
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    country: '',
    zip: ''
  });

  const [paymentData, setPaymentData] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardHolder: ''
  });

  useEffect(() => {
    if (location.state?.event) {
      setEvent(location.state.event);
      setLoading(false);
    } else {
      fetchEventById(id);
    }
  }, [id, location.state]);

  const fetchEventById = async (eventId) => {
    try {
      const response = await fetch(`/api/events/${eventId}`);
      if (!response.ok) {
        throw new Error(`Error ${response.status}: ${response.statusText}`);
      }

      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        throw new Error("Invalid response format. Expected JSON.");
      }

      const eventData = await response.json();
      setEvent(eventData);
    } catch (err) {
      setError(err.message);
      setTimeout(() => {
        history.push('/not-found');
      }, 3000);
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePaymentChange = (e) => {
    const { name, value } = e.target;
    setPaymentData(prev => ({ ...prev, [name]: value }));
  };

  const handleRegistrationSubmit = (e) => {
    e.preventDefault();
    setStep('payment');
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setPaymentSuccess(true);
      updateEventStatus(id);
    }, 1000);
  };

  const updateEventStatus = async (eventId) => {
    try {
      await fetch(`/api/events/${eventId}/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });
    } catch (err) {
      console.error('Error updating event status:', err);
    }
  };

  if (loading) {
    return <Loading />;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!event) {
    return <div>Event not found</div>;
  }

  return (
    <div className="registration-container">
      <Navbar />
      <main className="registration-content">
        <div className="registration-grid">
          <div className="registration-form">
            {step === 'registration' ? (
              <>
                <h2>Register for {event.title}</h2>
                <form onSubmit={handleRegistrationSubmit}>
                  <div className="registration-form-group">
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Full Name"
                      required
                    />
                  </div>
                  <div className="registration-form-group">
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="Phone Number"
                      required
                    />
                  </div>
                  <div className="registration-form-group">
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      placeholder="Email Address"
                      required
                    />
                  </div>
                  <button type="submit" className="registration-submit-button">
                    Continue to Payment
                  </button>
                </form>
              </>
            ) : (
              <>
                <h2>Payment for {event.title}</h2>
                {paymentSuccess ? (
                  <div className="payment-success">
                    <h3>Payment Successful!</h3>
                    <p>You are now registered for {event.title}</p>
                  </div>
                ) : (
                  <form onSubmit={handlePaymentSubmit}>
                    <button type="submit" className="registration-submit-button">
                      Complete Payment
                    </button>
                  </form>
                )}
              </>
            )}
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default RegistrationPage;
