import React, { useState } from 'react';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './Dashboard.css';

const Dashboard = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [activeSection, setActiveSection] = useState('registered');
  const [events, setEvents] = useState({
    registered: [],
    upcoming: [],
    past: []
  });

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleCreateEvent = () => {
    // Add event creation logic
    console.log('Create new event');
  };

  const renderEvents = () => {
    switch (activeSection) {
      case 'registered':
        return <div className="events-list">Registered Events Content</div>;
      case 'upcoming':
        return <div className="events-list">Upcoming Events Content</div>;
      case 'past':
        return <div className="events-list">Past Events Content</div>;
      default:
        return null;
    }
  };

  return (
    <div className="dashboard-container">
      <Navbar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Event Management Dashboard</h1>
          <div className="user-profile" onClick={toggleDropdown}>
            <span className="username">John Doe</span>
            {showDropdown && (
              <div className="dropdown-menu">
                <div className="dropdown-item">Accounts</div>
                <div className="dropdown-item">Events and All</div>
                <div className="dropdown-item">Logout</div>
              </div>
            )}
          </div>
        </div>

        <div className="events-navigation">
          <button
            className={`nav-button ${activeSection === 'registered' ? 'active' : ''}`}
            onClick={() => setActiveSection('registered')}
          >
            Registered Events
          </button>
          <button
            className={`nav-button ${activeSection === 'upcoming' ? 'active' : ''}`}
            onClick={() => setActiveSection('upcoming')}
          >
            Upcoming Events
          </button>
          <button
            className={`nav-button ${activeSection === 'past' ? 'active' : ''}`}
            onClick={() => setActiveSection('past')}
          >
            Past Events
          </button>
        </div>

        <div className="events-container">
          {renderEvents()}
        </div>

        <button className="create-event-button" onClick={handleCreateEvent}>
          Create New Event
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default Dashboard;
