import React, { useState } from 'react';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './Dashboard.css';

const Dashboard = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [events, setEvents] = useState({
    registered: [],
    upcoming: [],
    past: []
  });

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const handleCreateEvent = () => {
    // Add event creation logic
    console.log('Create new event');
  };

  return (
    <div className="dashboard-container">
      <Navbar />
      <div className="dashboard-content">
        <div className="dashboard-header">
          <h1>Event Management Dashboard</h1>
          <div className="user-profile" onClick={toggleDropdown}>
            <span className="username">John Doe</span>
            {showDropdown && (
              <div className="dropdown-menu">
                <div className="dropdown-item">Accounts</div>
                <div className="dropdown-item">Events and All</div>
                <div className="dropdown-item">Logout</div>
              </div>
            )}
          </div>
        </div>

        <div className="events-grid">
          <div className="events-section">
            <h2>Registered Events</h2>
            <div className="events-list">
              {events.registered.length === 0 && <p>No registered events</p>}
            </div>
          </div>

          <div className="events-section">
            <h2>Upcoming Events</h2>
            <div className="events-list">
              {events.upcoming.length === 0 && <p>No upcoming events</p>}
            </div>
          </div>

          <div className="events-section">
            <h2>Past Events</h2>
            <div className="events-list">
              {events.past.length === 0 && <p>No past events</p>}
            </div>
          </div>
        </div>

        <button className="create-event-button" onClick={handleCreateEvent}>
          Create New Event
        </button>
      </div>
      <Footer />
    </div>
  );
};

export default Dashboard;
