import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useHistory } from 'react-router-dom';
import Navbar from '../components/navbar';
import Footer from '../components/footer';
import './registrationPage.css';

const Loading = () => (
  <div className="loading-container">
    <div className="loading-circle"></div>
  </div>
);

const RegistrationPage = () => {
  const { title } = useParams();
  const location = useLocation();
  const history = useHistory();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [step, setStep] = useState('registration');
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
    city: '',
    country: '',
    zip: ''
  });

  useEffect(() => {
    console.log("Location State:", location.state);
    if (location.state?.event) {
      console.log("Using event from location state.");
      setEvent(location.state.event);
      setLoading(false);
    } else {
      console.log("Fetching event by title:", title);
      fetchEventByTitle(title);
    }
  }, [title, location.state]);

  const fetchEventByTitle = async (eventTitle) => {
    try {
        console.log("Fetching event for title:", eventTitle);

        const response = await fetch(`http://127.0.0.1:8000/api/events/${encodeURIComponent(eventTitle)}`)
        console.log("Raw Response Status:", response.status);
        console.log("Raw Response Headers:", response.headers.get("content-type"));

        // If response is not OK, log and throw an error
        if (!response.ok) {
            const textResponse = await response.text();
            console.error("Unexpected Response Content:", textResponse);
            throw new Error(`HTTP Error ${response.status}: ${response.statusText}`);
        }

        const contentType = response.headers.get("content-type");

        // Check if response is actually JSON
        if (!contentType || !contentType.includes("application/json")) {
            const textResponse = await response.text();  // Log response if not JSON
            console.error("Unexpected Response Format (Likely HTML):", textResponse);
            throw new Error("Invalid response format. Expected JSON but got HTML.");
        }

        const eventData = await response.json();
        console.log("Fetched event data:", eventData);
        setEvent(eventData);
    } catch (err) {
        console.error("Error fetching event:", err);
        setError(err.message);
        setTimeout(() => {
            history.push('/not-found');
        }, 3000);
    } finally {
        setLoading(false);
    }
};


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleRegistrationSubmit = (e) => {
    e.preventDefault();
    setStep('payment');
  };

  if (loading) {
    return <Loading />;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!event) {
    return <div>Event not found</div>;
  }

  return (
    <div className="registration-container">
      <Navbar />
      <main className="registration-content">
        <div className="registration-grid">
          <div className="registration-form">
            {step === 'registration' ? (
              <>
                <h2>Register for {event.title}</h2>
                <form onSubmit={handleRegistrationSubmit}>
                  <div className="registration-form-group">
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      placeholder="Full Name"
                      required
                    />
                  </div>
                  <div className="registration-form-group">
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      placeholder="Phone Number"
                      required
                    />
                  </div>
                  <div className="registration-form-group">
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      placeholder="Email Address"
                      required
                    />
                  </div>
                  <button type="submit" className="registration-submit-button">
                    Continue to Payment
                  </button>
                </form>
              </>
            ) : (
              <>
                <h2>Payment for {event.title}</h2>
                {paymentSuccess ? (
                  <div className="payment-success">
                    <h3>Payment Successful!</h3>
                    <p>You are now registered for {event.title}</p>
                  </div>
                ) : (
                  <form>
                    <button type="submit" className="registration-submit-button">
                      Complete Payment
                    </button>
                  </form>
                )}
              </>
            )}
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default RegistrationPage;
